import * as Types from '../../../constants/ActionType';

const initialState = 1;


export const total_revenue = (state = initialState , action) =>
{
  let newState = state;
  if(typeof action.type.total_revenue === "undefined")
  {
    return newState
  }
  else
  {
  switch(action.type.total_revenue.type){ 
  
    case Types.GET_TOTAL_REVENUE :
    
        newState = action.type.total_revenue.data;
        return newState;   

      default: return newState;
  }
}
}
