import * as Types from './../../../constants/ActionType';




export const totalPrice = (state = 0 , action) =>
{
    let newState = state;

  if(typeof action.type.payment_total === "undefined"){
    return newState
  }
  else
  {
  switch(action.type.payment_total.type){ 
    // case Types.FETCH_PRODUCTS:
    //   newState = action.product
    case Types.PAYMENT_TOTAL :
        newState = action.type.payment_total.data;
        return newState;   
        // case Types.DELETE_PRODUCT :
        //   newState = action.product;
        //   return newState; 
        //   case Types.UPDATE_PRODUCT :
        //     newState = action.product;
        //     return newState;     
      default: return newState;
  }
}
}
export const show_list_table = (state = [] , action) =>
{
  let newState = [...state];
  if(typeof action.type.feature_appendmenu === "undefined"){
    return newState
  }
  else
  {
  switch(action.type.feature_appendmenu.type){ 
    // case Types.FETCH_PRODUCTS:
    //   newState = action.product
    case Types.FEATURE_APPENDMENU :
        newState = action.type.feature_appendmenu.data;
        return newState;   
        // case Types.DELETE_PRODUCT :
        //   newState = action.product;
        //   return newState; 
        //   case Types.UPDATE_PRODUCT :
        //     newState = action.product;
        //     return newState;     
      default: return newState;
  }
}
}
