import callApi from './../ultis/apiCaller';

checkAuth = () =>{
   return callApi('auth', 'get', null).then(res =>{
        if(res.data.status)
        {
            return true;
        }
        else
        {
           return false;
        }
      
       
     });
}
var auth = checkAuth();
export default auth;
