import callApi from './../ultis/apiCaller';

function checkAuth(){
   return callApi('auth', 'get', null).then(res =>{
        if(res.data.status)
        {
            return true;
        }
        else
        {
           return false;
        }
      
       
     });
}
export default checkAuth;
