import { combineReducers } from 'redux';
import  rooms from './../Table/rooms';
import status from './../Table/status';
import stttable from './../Table/stttable';
import * as event from './../Table/eventtable';
import navigation from './../Table/navigation';
import * as form from './../Table/formtable';
//Edit Type
import * as modal from './../Table/modal'
import * as formRoom from './../Table/formroom'
import styleEvent from './../Table/styleevent'
import * as tap from './../Table/tap'
import * as table from './../Table/table';
import actionPrevent from './../Table/actionprevent';
const appReducers = combineReducers({
    table : combineReducers({
        index : table.index,
        count : table.count,
        data : table.data
    }),
    stttable,
    event : combineReducers ({
       room : event.room,
        table: event.table,
        status : event.status
    }),
    navigation,
    formtable : combineReducers({
        idtable : form.idtable,
        name : form.name,
        idarea : form.idarea,
        status : form.status,
        bill : form.bill
    }),

    /////////// Edit Reducers (ROOM) ///////////////////////

    showModal : combineReducers({
        mTable : combineReducers({
            text : modal.t_Text,
            classOntap : modal.t_Ontap,
        }),
        mRoom : combineReducers({
            text : modal.r_Text,
            classOntap : modal.r_Ontap
        }),
    
    }),
    formroom : combineReducers({
        room : formRoom.room,
        name : formRoom.name,
        id : formRoom.id,
        onSearch : formRoom.search

    }),
    eventUpdate_Insert : styleEvent,
    rooms ,
    status,
    onTap : combineReducers({
        room : tap.room,
        table : tap.table,
        btnRoom : tap.btnRoom,
        btnTable : tap.btnTable

    }),

    //event preventDefault
    actionPrevent,
   
});

export default appReducers;
