import { connect } from 'react-redux';
import checkAuth from './../../authentication/checkauth';





class DashBoard extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      redirect : true,
      loading : false,
      
    };
  }

  
  componentWillMount(){
    CheckAuth().then((data)=>{
      if(data == true)
      {
        console.log(data);
         this.setState({redirect : true, loading : true});
        //this.state.redirect = true;
        return;
