  {
    return (
     
       
       	<div class="row list-filter filter">
					<div class="col-md list-filter-content">
					
								{this.showList_Areas(this.props.area.areas)}
						
					</div>
				</div>

    );
  }
}
const  mapStateToProps = state =>{
	return{
	 area : state.pos
	}
  };
export default connect(mapStateToProps,mapDispatchToProps)(Menu);
