// import './../../../bootstrap/dist/css/bootstrap-theme.min.css';
// import './../../../bootstrap/dist/css/bootstrap.min.css';
// import './../../../bootstrap/dist/js/bootstrap.min.js';






class Table extends Component {
  
  redirect_func = (data) =>{
    this.props.redirect_page({
      type : {
        redirect : {
          type : 'REDIRECT_PAGES',
          data : data
        }
      }
    })
  }

  componentWillMount(){
    if(this.props.redirect =='not_verfication' )
    {
      console.log('checkauth')
      CheckAuth().then((data)=>{
        console.log(data);
        
        if(data == true)
        {
         this.redirect_func(true)
          return;
        }
        else
        {
         this.redirect_func(false)
        }
      })
    }

   
   
  }
 
  
 
  render() 
  
  {
   if(this.props.redirect == 'not_verfication')
   {
     return <div></div>
   }
  if(this.props.redirect== false)
  {
    return <Redirect to={{
      pathname: '/login',
      
  }}
/>  }
    
    return (
    
<div>
  
<Header/>

<div class="row sidebar-row">

<div class="col-xs-2 col-sm-2 col-md-2 col-lg-2 full-height"  >

			<Menu/>
	
    <div class="col-xs-2 col-sm-2 col-md-2 col-lg-2"  >



    </div>


	
</div>

<Content/>



</div>
</div>
   

   
    );
  }
  
}
const  mapStateToProps = state =>{
  
  console.log(state);
    return{
     redirect : state.table.redirect
  
    }
  };
  
  
  const mapDispatchToProps = (dispatch, props) =>{
    return {
      redirect_page : (tap) =>{
        dispatch(tap)
      },
      
    }
  }

    export default connect(mapStateToProps,mapDispatchToProps)(Table);
