import React, { Component } from 'react';
import {connect} from 'react-redux';

import * as action from '../../actions/pos';


///////







///////////
class Menu extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
     style_Default : {
       backgroundColor : '',
     },
     style_Select : {
      backgroundColor : '#c62253',
     },
     choose : 0
    };
  }
   // console.log(this.props.history.match.params.id);
 onclickButonofAreas = (id) =>{
  this.setState({choose : id})
 }
   showList_Areas = (areas) =>
   {
     console.log(areas);
   var result = null;
   var style = {width : '100px'}
   if(areas.length > 0)
   {
     
    result = areas.map((area,index) =>
     {
      if(area.id == this.state.choose)
      {
        return (

					
          <button class="btn btn-primary col-3 size-margin-button" style = {this.state.style_Select}>{area.BranchName}</button>
               
           
           
            );
      }
     return(
      <button class="btn btn-primary col-3 size-margin-button" style = {this.state.style_Default}>{area.BranchName}</button>
           
     )
   
     })
   }
   else
   {
     var Undefined_ = [];
     for(var i = 0; i< 8 ; i++)
     {
      Undefined_[i] = <button class="btn btn-primary col-3 size-margin-button">Undefined!!</button>
        
     }
     result = Undefined_
    return result
   }
   return result;
   };
   componentWillMount(){
     this.props.onfetch_area()
    
   }
   componentDidMount(){
     this.setState({choose : this.props.area.areas[0].id})
   }
  render() 
  
  {
    return (
     
       
       	<div class="row list-filter filter">
           
					<div class="col-md list-filter-content">
					
								{this.showList_Areas(this.props.area.areas)}
						
					</div>
				</div>

    );
  }
}
const mapDispatchToProps = (dispatch, props) =>{
  return {
	onfetch_area: () =>{
		dispatch(action.fetchAreaONrequest())
	},
	
}
}
const  mapStateToProps = state =>{
	return{
	 area : state.pos
	}
  };
export default connect(mapStateToProps,mapDispatchToProps)(Menu);
